import React, { useEffect, useState } from 'react';
import './App.css';
import GaugeChart from 'react-gauge-chart';
import logo from './assets/logo.png'; // Assuming you have a logo image in the src folder

function App() {
  const [gateway, setGateway] = useState('');
  const [downloadSpeed, setDownloadSpeed] = useState(null);
  const [uploadSpeed, setUploadSpeed] = useState(null);
  const [ping, setPing] = useState(null);
  const [scanResults, setScanResults] = useState([]);
  const [isScanning, setIsScanning] = useState(false);
  const [showScanResults, setShowScanResults] = useState(true);
  const [hostCount, setHostCount] = useState(0);

  useEffect(() => {
    fetch('/gateway')
      .then((res) => res.json())
      .then((data) => setGateway(data.gateway_ip || 'Unknown'))
      .catch(() => setGateway('Error'));
  }, []);

  useEffect(() => {
    runSpeedtest(); // initial run
    const interval = setInterval(runSpeedtest, 120_000); // run every 2 minutes
    return () => clearInterval(interval); // cleanup
  }, []);

  const runSpeedtest = async () => {
    const res = await fetch('/speedtest');
    const data = await res.json();
    if (!data.error) {
      setDownloadSpeed(data.download_mbps);
      setUploadSpeed(data.upload_mbps);
      setPing(data.ping_ms);
    }
  };

  const runScan = async () => {
    setIsScanning(true);
    try {
      const res = await fetch('/scan');
      const data = await res.json();

      if (Array.isArray(data.results)) {
        setScanResults(data.results);
        setHostCount(data.host_count);
      } else {
        console.error('Invalid scan results format:', data);
        setScanResults([]);
      }
    } catch (error) {
      console.error('Scan failed:', error);
      setScanResults([]);
    }
    setIsScanning(false);
  };

  return (
    <div className='App'>
      <header className='header'>
        <img src={logo} alt='NetWatchdog Logo' className='logo' />
        <h1>NetWatchdog</h1>
        <p>Default Gateway: {gateway}</p>
        <div className='button-group'>
          <button onClick={runSpeedtest}>Run Speedtest</button>
          <button onClick={runScan}>Scan Network</button>
        </div>
      </header>

      <main>
        <div className='gauges'>
          <div className='gauge-container'>
            <h3>Download</h3>
            <GaugeChart
              id='download-gauge'
              nrOfLevels={30}
              percent={(downloadSpeed || 0) / 1000}
              text={`${downloadSpeed ?? '--'} Mbps`}
              animate
              needleColor='#fff'
              colors={['#00ff99', '#0099ff']}
            />
          </div>
          <div className='gauge-container'>
            <h3>Upload</h3>
            <GaugeChart
              id='upload-gauge'
              nrOfLevels={30}
              percent={(uploadSpeed || 0) / 100}
              text={`${uploadSpeed ?? '--'} Mbps`}
              animate
              needleColor='#fff'
              colors={['#ffcc00', '#ff0066']}
            />
          </div>
          <div className='gauge-container'>
            <h3>Ping</h3>
            <GaugeChart
              id='ping-gauge'
              nrOfLevels={30}
              percent={(ping || 0) / 500}
              text={`${ping ?? '--'} ms`}
              animate
              needleColor='#fff'
              colors={['#cccccc', '#ff3333']}
            />
          </div>
        </div>

        <div className='scan-section'>
          <div className='scan-header'>
            <h2>Scan Results</h2>
            <button onClick={() => setShowScanResults(!showScanResults)}>
              {showScanResults ? 'Hide' : 'Show'}
            </button>
          </div>
          {isScanning && <p>Scanning network...</p>}
          {showScanResults && !isScanning && (
            <div className='scan-results'>
              {scanResults.length === 0 ? (
                <p>No results yet.</p>
              ) : (
                scanResults.map((host, index) => (
                  <div key={index} className='scan-entry'>
                    <strong>{host.ip}</strong>
                    <span>Open Ports: {host.open_ports.join(', ')}</span>
                    <p>Hosts Scanned: {hostCount}</p>
                  </div>
                ))
              )}
            </div>
          )}
        </div>
      </main>
    </div>
  );
}

export default App;
