import React, { useState, useEffect } from 'react';
import Speedometer from 'react-d3-speedometer';
import './App.css';

function App() {
  const [speed, setSpeed] = useState(null);
  const [scanResults, setScanResults] = useState([]);
  const [gateway, setGateway] = useState('');
  const [showResults, setShowResults] = useState(true);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetch('/gateway')
      .then((res) => res.json())
      .then((data) => setGateway(data.gateway_ip || 'Unavailable'));
  }, []);

  const runSpeedtest = () => {
    fetch('/speedtest')
      .then((res) => res.json())
      .then((data) => setSpeed(data));
  };

  const runScan = () => {
    setLoading(true);
    fetch('/scan')
      .then((res) => res.json())
      .then((data) => {
        setScanResults(data.results);
        setLoading(false);
      });
  };

  return (
    <div className='app'>
      <header className='header'>
        <img src={logo} alt='NetWatchdog Logo' className='logo' />
        <h1>NetWatchdog</h1>
        <p>Default Gateway: {gateway}</p>
        <div className='button-group'>
          <button onClick={runSpeedtest}>Run Speedtest</button>
          <button onClick={runScan}>Scan Network</button>
        </div>
      </header>

      {speed && (
        <div className='gauges'>
          <div className='gauge'>
            <Speedometer
              value={speed.download_mbps}
              maxValue={1000}
              needleColor='white'
              startColor='green'
              endColor='red'
              segments={10}
              height={200}
              animate={true}
            />
            <p>Download: {speed.download_mbps} Mbps</p>
          </div>
          <div className='gauge'>
            <Speedometer
              value={speed.upload_mbps}
              maxValue={100}
              needleColor='white'
              startColor='blue'
              endColor='orange'
              segments={10}
              height={200}
              animate={true}
            />
            <p>Upload: {speed.upload_mbps} Mbps</p>
          </div>
        </div>
      )}

      <div className='scan-results'>
        <button className='toggle-btn' onClick={() => setShowResults(!showResults)}>
          {showResults ? 'Hide' : 'Show'} Scan Results
        </button>
        {loading ? (
          <p>Scanning...</p>
        ) : (
          showResults && (
            <ul>
              {scanResults.map((host, index) => (
                <li key={index}>
                  {host.ip} â†’ Ports: {host.open_ports.join(', ')}
                </li>
              ))}
            </ul>
          )
        )}
      </div>
    </div>
  );
}

export default App;
