import React, { useEffect, useState } from 'react';
import Speedometer from 'react-d3-speedometer';

const App = () => {
  const [scanData, setScanData] = useState(null);
  const [speedData, setSpeedData] = useState(null);
  const [gatewayData, setGatewayData] = useState(null);
  const [errors, setErrors] = useState([]);
  const [isScanning, setIsScanning] = useState(false);

  const fetchScan = async () => {
    try {
      setIsScanning(true);
      const res = await fetch('http://localhost:8181/scan');
      const data = await res.json();
      setScanData(data);
    } catch (err) {
      console.error('Scan error:', err);
      setErrors((prev) => [...prev, 'Scan']);
    } finally {
      setIsScanning(false);
    }
  };

  useEffect(() => {
    fetch('http://localhost:8181/speedtest')
      .then((res) => res.json())
      .then((data) => setSpeedData(data))
      .catch((err) => {
        console.error('Speedtest error:', err);
        setErrors((prev) => [...prev, 'Speedtest']);
      });

    fetch('http://localhost:8181/gateway')
      .then((res) => res.json())
      .then((data) => setGatewayData(data))
      .catch((err) => {
        console.error('Gateway error:', err);
        setErrors((prev) => [...prev, 'Gateway']);
      });
  }, []);

  return (
    <div
      style={{ background: '#121212', color: '#fff', fontFamily: 'sans-serif', padding: '2rem' }}
    >
      <h1>NetWatchdog Dashboard</h1>

      <h2>Network Scan</h2>
      <button
        onClick={fetchScan}
        style={{
          padding: '0.5rem 1rem',
          marginBottom: '1rem',
          cursor: 'pointer',
          background: '#444',
          color: '#fff',
          border: 'none',
          borderRadius: '5px',
        }}
      >
        Run Network Scan
      </button>
      {isScanning ? (
        <p>Scanning...</p>
      ) : scanData === null ? (
        errors.includes('Scan') ? (
          <p>Error loading scan data</p>
        ) : (
          <p>No scan data</p>
        )
      ) : scanData.results.length === 0 ? (
        <p>No open ports or devices found.</p>
      ) : (
        <>
          <p>Hosts Scanned: {scanData.host_count}</p>
          <ul>
            {scanData.results.map((entry, i) => (
              <li key={i}>
                <strong>{entry.ip}</strong>: Ports â†’ {entry.open_ports.join(', ')}
              </li>
            ))}
          </ul>
        </>
      )}

      <h2>Speed Test</h2>
      {speedData === null ? (
        errors.includes('Speedtest') ? (
          <p>Error loading speed test</p>
        ) : (
          <p>Loading...</p>
        )
      ) : (
        <div style={{ display: 'flex', gap: '2rem', alignItems: 'center', flexWrap: 'wrap' }}>
          <div>
            <Speedometer
              value={speedData.download_mbps}
              minValue={0}
              maxValue={1000}
              needleColor='#fff'
              startColor='red'
              endColor='green'
              segments={10}
              textColor='#fff'
              height={200}
              width={300}
            />
            <p style={{ textAlign: 'center' }}>Download: {speedData.download_mbps} Mbps</p>
          </div>
          <div>
            <Speedometer
              value={speedData.upload_mbps}
              minValue={0}
              maxValue={1000}
              needleColor='#fff'
              startColor='red'
              endColor='blue'
              segments={10}
              textColor='#fff'
              height={200}
              width={300}
            />
            <p style={{ textAlign: 'center' }}>Upload: {speedData.upload_mbps} Mbps</p>
          </div>
          <div>
            <Speedometer
              value={speedData.ping_ms}
              minValue={0}
              maxValue={100}
              needleColor='#fff'
              startColor='green'
              endColor='red'
              segments={5}
              textColor='#fff'
              height={200}
              width={300}
            />
            <p style={{ textAlign: 'center' }}>Ping: {speedData.ping_ms} ms</p>
          </div>
        </div>
      )}

      <h2>Gateway Info</h2>
      {gatewayData === null ? (
        errors.includes('Gateway') ? (
          <p>Error loading gateway info</p>
        ) : (
          <p>Loading...</p>
        )
      ) : (
        <div
          style={{
            backgroundColor: '#1e1e1e',
            padding: '1rem',
            borderRadius: '6px',
            overflowX: 'auto',
            whiteSpace: 'pre-wrap',
            fontFamily: 'monospace',
            lineHeight: '1.5',
          }}
        >
          {JSON.stringify(gatewayData, null, 2)}
        </div>
      )}
    </div>
  );
};

export default App;
