import React, { useEffect, useState } from 'react';
import Speedometer from 'react-d3-speedometer';

const App = () => {
  const [scanData, setScanData] = useState(null);
  const [speedData, setSpeedData] = useState(null);
  const [gatewayData, setGatewayData] = useState(null);
  const [errors, setErrors] = useState([]);
  const [scanning, setScanning] = useState(false);

  const fetchScan = () => {
    setScanning(true);
    fetch('http://localhost:8181/scan')
      .then((res) => res.json())
      .then((data) => {
        setScanData(data);
        setErrors((prev) => prev.filter((e) => e !== 'Scan'));
      })
      .catch((err) => {
        console.error('Scan error:', err);
        setErrors((prev) => [...new Set([...prev, 'Scan'])]);
      })
      .finally(() => setScanning(false));
  };

  useEffect(() => {
    fetch('http://localhost:8181/speedtest')
      .then((res) => res.json())
      .then((data) => setSpeedData(data))
      .catch((err) => {
        console.error('Speedtest error:', err);
        setErrors((prev) => [...prev, 'Speedtest']);
      });

    fetch('http://localhost:8181/gateway')
      .then((res) => res.json())
      .then((data) => setGatewayData(data))
      .catch((err) => {
        console.error('Gateway error:', err);
        setErrors((prev) => [...prev, 'Gateway']);
      });
  }, []);

  return (
    <div
      style={{ background: '#121212', color: '#fff', fontFamily: 'sans-serif', padding: '2rem' }}
    >
      <h1>NetWatchdog Dashboard</h1>

      <h2>Network Scan</h2>
      <button onClick={fetchScan} disabled={scanning}>
        {scanning ? 'Scanning...' : 'Run Network Scan'}
      </button>
      {scanData === null ? (
        scanning ? (
          <p>Scanning network...</p>
        ) : errors.includes('Scan') ? (
          <p>Error loading scan data</p>
        ) : (
          <p>No scan run yet</p>
        )
      ) : scanData.hosts?.length === 0 ? (
        <p>No hosts found.</p>
      ) : (
        <>
          <p>Found {scanData.hosts.length} hosts</p>
          <ul>
            {scanData.hosts.map((entry, i) => (
              <li key={i}>
                <strong>{entry.ip}</strong>: Ports â†’ {entry.open_ports.join(', ')}
              </li>
            ))}
          </ul>
        </>
      )}

      <h2>Speed Test</h2>
      {speedData === null ? (
        errors.includes('Speedtest') ? (
          <p>Error loading speed test</p>
        ) : (
          <p>Loading speed test...</p>
        )
      ) : (
        <>
          <div style={{ display: 'flex', gap: '2rem', flexWrap: 'wrap' }}>
            <div>
              <Speedometer
                value={speedData.download_mbps}
                maxValue={1000}
                needleColor='white'
                startColor='green'
                endColor='red'
                textColor='#fff'
                height={180}
                labelFontSize='12px'
                ringWidth={30}
                segments={10}
              />
              <p style={{ textAlign: 'center' }}>Download: {speedData.download_mbps} Mbps</p>
            </div>
            <div>
              <Speedometer
                value={speedData.upload_mbps}
                maxValue={1000}
                needleColor='white'
                startColor='blue'
                endColor='red'
                textColor='#fff'
                height={180}
                labelFontSize='12px'
                ringWidth={30}
                segments={10}
              />
              <p style={{ textAlign: 'center' }}>Upload: {speedData.upload_mbps} Mbps</p>
            </div>
          </div>
          <ul>
            <li>Download: {speedData.download_mbps} Mbps</li>
            <li>Upload: {speedData.upload_mbps} Mbps</li>
            <li>Ping: {speedData.ping_ms} ms</li>
          </ul>
        </>
      )}

      <h2>Gateway Info</h2>
      {gatewayData === null ? (
        errors.includes('Gateway') ? (
          <p>Error loading gateway info</p>
        ) : (
          <p>Loading gateway...</p>
        )
      ) : (
        <pre>{JSON.stringify(gatewayData, null, 2)}</pre>
      )}
    </div>
  );
};

export default App;
